            | ( ( uint(block.blockhash(_lastb+2)) & 0xFFFFFF ) << 48 )

            | ( ( uint(block.blockhash(_lastb+3)) & 0xFFFFFF ) << 72 )

            | ( ( uint(block.blockhash(_lastb+4)) & 0xFFFFFF ) << 96 )

            | ( ( uint(block.blockhash(_lastb+5)) & 0xFFFFFF ) << 120 )

