            | ( ( uint(block.blockhash(_lastb+7)) & 0xFFFFFF ) << 168 )

            | ( ( uint(block.blockhash(_lastb+8)) & 0xFFFFFF ) << 192 )

            | ( ( uint(block.blockhash(_lastb+9)) & 0xFFFFFF ) << 216 )
            | ( ( uint(_delta) / hashesSize) << 240));
    }

