contract PandaOwnership is PandaBase, ERC721 {


    string public constant name = "PandaEarth";
    string public constant symbol = "PE";

    bytes4 constant InterfaceSignature_ERC165 =
        bytes4(keccak256('supportsInterface(bytes4)'));

    bytes4 constant InterfaceSignature_ERC721 =
        bytes4(keccak256('name()')) ^
        bytes4(keccak256('symbol()')) ^
        bytes4(keccak256('totalSupply()')) ^
        bytes4(keccak256('balanceOf(address)')) ^
        bytes4(keccak256('ownerOf(uint256)')) ^
        bytes4(keccak256('approve(address,uint256)')) ^
        bytes4(keccak256('transfer(address,uint256)')) ^
        bytes4(keccak256('transferFrom(address,address,uint256)')) ^
        bytes4(keccak256('tokensOfOwner(address)')) ^
        bytes4(keccak256('tokenMetadata(uint256,string)'));




    function supportsInterface(bytes4 _interfaceID) external view returns (bool)
    {



        return ((_interfaceID == InterfaceSignature_ERC165) || (_interfaceID == InterfaceSignature_ERC721));
    }








    function _owns(address _claimant, uint256 _tokenId) internal view returns (bool) {
        return pandaIndexToOwner[_tokenId] == _claimant;
    }




    function _approvedFor(address _claimant, uint256 _tokenId) internal view returns (bool) {
        return pandaIndexToApproved[_tokenId] == _claimant;
    }






    function _approve(uint256 _tokenId, address _approved) internal {
        pandaIndexToApproved[_tokenId] = _approved;
    }




    function balanceOf(address _owner) public view returns (uint256 count) {
        return ownershipTokenCount[_owner];
    }







    function transfer(
        address _to,
        uint256 _tokenId
    )
        external
        whenNotPaused
    {

        require(_to != address(0));



        require(_to != address(this));



        require(_to != address(saleAuction));
        require(_to != address(siringAuction));


        require(_owns(msg.sender, _tokenId));


        _transfer(msg.sender, _to, _tokenId);
    }







    function approve(
        address _to,
        uint256 _tokenId
    )
        external
        whenNotPaused
    {

        require(_owns(msg.sender, _tokenId));


        _approve(_tokenId, _to);


        Approval(msg.sender, _to, _tokenId);
    }








    function transferFrom(
        address _from,
        address _to,
        uint256 _tokenId
    )
        external
        whenNotPaused
    {

        require(_to != address(0));



        require(_to != address(this));

        require(_approvedFor(msg.sender, _tokenId));
        require(_owns(_from, _tokenId));


        _transfer(_from, _to, _tokenId);
    }



    function totalSupply() public view returns (uint) {
        return pandas.length - 1;
    }



    function ownerOf(uint256 _tokenId)
        external
        view
        returns (address owner)
    {
        owner = pandaIndexToOwner[_tokenId];

        require(owner != address(0));
    }







    function tokensOfOwner(address _owner) external view returns(uint256[] ownerTokens) {
        uint256 tokenCount = balanceOf(_owner);

        if (tokenCount == 0) {

            return new uint256[](0);
        } else {
            uint256[] memory result = new uint256[](tokenCount);
            uint256 totalCats = totalSupply();
            uint256 resultIndex = 0;



            uint256 catId;

            for (catId = 1; catId <= totalCats; catId++) {
                if (pandaIndexToOwner[catId] == _owner) {
                    result[resultIndex] = catId;
                    resultIndex++;
                }
            }

            return result;
        }
    }




    function _memcpy(uint _dest, uint _src, uint _len) private view {

        for(; _len >= 32; _len -= 32) {
            assembly {
                mstore(_dest, mload(_src))
            }
            _dest += 32;
            _src += 32;
        }


        uint256 mask = 256 ** (32 - _len) - 1;
        assembly {
            let srcpart := and(mload(_src), not(mask))
            let destpart := and(mload(_dest), mask)
            mstore(_dest, or(destpart, srcpart))
        }
    }




    function _toString(bytes32[4] _rawBytes, uint256 _stringLength) private view returns (string) {
        var outputString = new string(_stringLength);
        uint256 outputPtr;
        uint256 bytesPtr;

        assembly {
            outputPtr := add(outputString, 32)
            bytesPtr := _rawBytes
        }

        _memcpy(outputPtr, bytesPtr, _stringLength);

        return outputString;
    }

}







contract PandaBreeding is PandaOwnership {

    uint256 public constant GENSIS_TOTAL_COUNT = 100;



    event Pregnant(address owner, uint256 matronId, uint256 sireId, uint256 cooldownEndBlock);

    event Abortion(address owner, uint256 matronId, uint256 sireId);




    uint256 public autoBirthFee = 2 finney;


    uint256 public pregnantPandas;

    mapping(uint256 => address) childOwner;




    function setGeneScienceAddress(address _address) external onlyCEO {
        GeneScienceInterface candidateContract = GeneScienceInterface(_address);


        require(candidateContract.isGeneScience());


        geneScience = candidateContract;
    }




    function _isReadyToBreed(Panda _kit) internal view returns(bool) {



        return (_kit.siringWithId == 0) && (_kit.cooldownEndBlock <= uint64(block.number));
    }




    function _isSiringPermitted(uint256 _sireId, uint256 _matronId) internal view returns(bool) {
        address matronOwner = pandaIndexToOwner[_matronId];
        address sireOwner = pandaIndexToOwner[_sireId];



        return (matronOwner == sireOwner || sireAllowedToAddress[_sireId] == matronOwner);
    }




    function _triggerCooldown(Panda storage _kitten) internal {

        _kitten.cooldownEndBlock = uint64((cooldowns[_kitten.cooldownIndex] / secondsPerBlock) + block.number);





        if (_kitten.cooldownIndex < 8 && geneScience.getWizzType(_kitten.genes) != 1) {
            _kitten.cooldownIndex += 1;
        }
    }





    function approveSiring(address _addr, uint256 _sireId)
    external
    whenNotPaused {
        require(_owns(msg.sender, _sireId));
        sireAllowedToAddress[_sireId] = _addr;
    }




    function setAutoBirthFee(uint256 val) external onlyCOO {
        autoBirthFee = val;
    }



    function _isReadyToGiveBirth(Panda _matron) private view returns(bool) {
        return (_matron.siringWithId != 0) && (_matron.cooldownEndBlock <= uint64(block.number));
    }




    function isReadyToBreed(uint256 _pandaId)
    public
    view
    returns(bool) {
        require(_pandaId > 0);
        Panda storage kit = pandas[_pandaId];
        return _isReadyToBreed(kit);
    }



    function isPregnant(uint256 _pandaId)
    public
    view
    returns(bool) {
        require(_pandaId > 0);

        return pandas[_pandaId].siringWithId != 0;
    }







    function _isValidMatingPair(
        Panda storage _matron,
        uint256 _matronId,
        Panda storage _sire,
        uint256 _sireId
    )
    private
    view
    returns(bool) {

        if (_matronId == _sireId) {
            return false;
        }


        if (_matron.matronId == _sireId || _matron.sireId == _sireId) {
            return false;
        }
        if (_sire.matronId == _matronId || _sire.sireId == _matronId) {
            return false;
        }



        if (_sire.matronId == 0 || _matron.matronId == 0) {
            return true;
        }


        if (_sire.matronId == _matron.matronId || _sire.matronId == _matron.sireId) {
            return false;
        }
        if (_sire.sireId == _matron.matronId || _sire.sireId == _matron.sireId) {
            return false;
        }


        if (geneScience.getSex(_matron.genes) + geneScience.getSex(_sire.genes) != 1) {
            return false;
        }


        return true;
    }



    function _canBreedWithViaAuction(uint256 _matronId, uint256 _sireId)
    internal
    view
    returns(bool) {
        Panda storage matron = pandas[_matronId];
        Panda storage sire = pandas[_sireId];
        return _isValidMatingPair(matron, _matronId, sire, _sireId);
    }







    function canBreedWith(uint256 _matronId, uint256 _sireId)
    external
    view
    returns(bool) {
        require(_matronId > 0);
        require(_sireId > 0);
        Panda storage matron = pandas[_matronId];
        Panda storage sire = pandas[_sireId];
        return _isValidMatingPair(matron, _matronId, sire, _sireId) &&
            _isSiringPermitted(_sireId, _matronId);
    }

    function _exchangeMatronSireId(uint256 _matronId, uint256 _sireId) internal returns(uint256, uint256) {
        if (geneScience.getSex(pandas[_matronId].genes) == 1) {
            return (_sireId, _matronId);
        } else {
            return (_matronId, _sireId);
        }
    }



    function _breedWith(uint256 _matronId, uint256 _sireId, address _owner) internal {

        (_matronId, _sireId) = _exchangeMatronSireId(_matronId, _sireId);

        Panda storage sire = pandas[_sireId];
        Panda storage matron = pandas[_matronId];


        matron.siringWithId = uint32(_sireId);


        _triggerCooldown(sire);
        _triggerCooldown(matron);



        delete sireAllowedToAddress[_matronId];
        delete sireAllowedToAddress[_sireId];


        pregnantPandas++;

        childOwner[_matronId] = _owner;


        Pregnant(pandaIndexToOwner[_matronId], _matronId, _sireId, matron.cooldownEndBlock);
    }






    function breedWithAuto(uint256 _matronId, uint256 _sireId)
    external
    payable
    whenNotPaused {

        require(msg.value >= autoBirthFee);


        require(_owns(msg.sender, _matronId));















        require(_isSiringPermitted(_sireId, _matronId));


        Panda storage matron = pandas[_matronId];


        require(_isReadyToBreed(matron));


        Panda storage sire = pandas[_sireId];


        require(_isReadyToBreed(sire));


        require(_isValidMatingPair(
            matron,
            _matronId,
            sire,
            _sireId
        ));


        _breedWith(_matronId, _sireId, msg.sender);
    }









    function giveBirth(uint256 _matronId, uint256[2] _childGenes, uint256[2] _factors)
    external
    whenNotPaused
    onlyCLevel
    returns(uint256) {

        Panda storage matron = pandas[_matronId];


        require(matron.birthTime != 0);


        require(_isReadyToGiveBirth(matron));


        uint256 sireId = matron.siringWithId;
        Panda storage sire = pandas[sireId];


        uint16 parentGen = matron.generation;
        if (sire.generation > matron.generation) {
            parentGen = sire.generation;
        }



        uint256[2] memory childGenes = _childGenes;

        uint256 kittenId = 0;


        uint256 probability = (geneScience.getPureFromGene(matron.genes) + geneScience.getPureFromGene(sire.genes)) / 2 + _factors[0];
        if (probability >= (parentGen + 1) * _factors[1]) {
            probability = probability - (parentGen + 1) * _factors[1];
        } else {
            probability = 0;
        }
        if (parentGen == 0 && gen0CreatedCount == GEN0_TOTAL_COUNT) {
            probability = 0;
        }
        if (uint256(keccak256(block.blockhash(block.number - 2), now)) % 100 < probability) {

            address owner = childOwner[_matronId];
            kittenId = _createPanda(_matronId, matron.siringWithId, parentGen + 1, childGenes, owner);
        } else {
            Abortion(pandaIndexToOwner[_matronId], _matronId, sireId);
        }







        delete matron.siringWithId;


        pregnantPandas--;



        msg.sender.send(autoBirthFee);

        delete childOwner[_matronId];


        return kittenId;
    }
}








contract ClockAuctionBase {


    struct Auction {

        address seller;

        uint128 startingPrice;

        uint128 endingPrice;

        uint64 duration;


        uint64 startedAt;

        uint64 isGen0;
    }


    ERC721 public nonFungibleContract;



    uint256 public ownerCut;


    mapping (uint256 => Auction) tokenIdToAuction;

    event AuctionCreated(uint256 tokenId, uint256 startingPrice, uint256 endingPrice, uint256 duration);
    event AuctionSuccessful(uint256 tokenId, uint256 totalPrice, address winner);
    event AuctionCancelled(uint256 tokenId);




    function _owns(address _claimant, uint256 _tokenId) internal view returns (bool) {
        return (nonFungibleContract.ownerOf(_tokenId) == _claimant);
    }





    function _escrow(address _owner, uint256 _tokenId) internal {

        nonFungibleContract.transferFrom(_owner, this, _tokenId);
    }





    function _transfer(address _receiver, uint256 _tokenId) internal {

        nonFungibleContract.transfer(_receiver, _tokenId);
    }





    function _addAuction(uint256 _tokenId, Auction _auction) internal {


        require(_auction.duration >= 1 minutes);

        tokenIdToAuction[_tokenId] = _auction;

        AuctionCreated(
            uint256(_tokenId),
            uint256(_auction.startingPrice),
            uint256(_auction.endingPrice),
            uint256(_auction.duration)
        );
    } 


    function _cancelAuction(uint256 _tokenId, address _seller) internal {
        _removeAuction(_tokenId);
        _transfer(_seller, _tokenId);
        AuctionCancelled(_tokenId);
    }



    function _bid(uint256 _tokenId, uint256 _bidAmount)
        internal
        returns (uint256)
    {

        Auction storage auction = tokenIdToAuction[_tokenId];





        require(_isOnAuction(auction));


        uint256 price = _currentPrice(auction);
        require(_bidAmount >= price);



        address seller = auction.seller;



        _removeAuction(_tokenId);


        if (price > 0) {



            uint256 auctioneerCut = _computeCut(price);
            uint256 sellerProceeds = price - auctioneerCut;









            seller.transfer(sellerProceeds);
        }





        uint256 bidExcess = _bidAmount - price;




        msg.sender.transfer(bidExcess);


        AuctionSuccessful(_tokenId, price, msg.sender);

        return price;
    }





    function _removeAuction(uint256 _tokenId) internal {
        delete tokenIdToAuction[_tokenId];
    }



    function _isOnAuction(Auction storage _auction) internal view returns (bool) {
        return (_auction.startedAt > 0);
    }





    function _currentPrice(Auction storage _auction)
        internal
        view
        returns (uint256)
    {
        uint256 secondsPassed = 0;




        if (now > _auction.startedAt) {
            secondsPassed = now - _auction.startedAt;
        }

        return _computeCurrentPrice(
            _auction.startingPrice,
            _auction.endingPrice,
            _auction.duration,
            secondsPassed
        );
    }





    function _computeCurrentPrice(
        uint256 _startingPrice,
        uint256 _endingPrice,
        uint256 _duration,
        uint256 _secondsPassed
    )
        internal
        pure
        returns (uint256)
    {





        if (_secondsPassed >= _duration) {


            return _endingPrice;
        } else {


            int256 totalPriceChange = int256(_endingPrice) - int256(_startingPrice);




            int256 currentPriceChange = totalPriceChange * int256(_secondsPassed) / int256(_duration);



            int256 currentPrice = int256(_startingPrice) + currentPriceChange;

            return uint256(currentPrice);
        }
    }



    function _computeCut(uint256 _price) internal view returns (uint256) {





        return _price * ownerCut / 10000;
    }

}





